import React, { Component } from 'react';
import './Meeting.css';
import { ReactMic } from 'react-mic';

class Meeting extends Component {

    constructor(props) {
        super(props);
        this.state = {
            record: false
        }

    }

    startRecording = () => {
        this.setState({
            record: true
        });
    }

    stopRecording = () => {
        this.setState({
            record: false
        });
    }

    onData(recordedBlob) {
        console.log('chunk of real-time data is: ', recordedBlob);
    }

    onStop(recordedBlob) {
        console.log('recordedBlob is: ', recordedBlob.blobURL);
        console.log(typeof (recordedBlob));
        // var blob = new Blob(recordedBlob);
        // console.log(blob);
        // var audio = document.getElementById("audio");
        // var file = new File([recordedBlob], "temp", {type: "audio/webm", lastModified: Date.now()});
        // console.log(file.type)
        // audio.src = window.URL.createObjectURL(recordedBlob);
        // audio.srcObject = recordedBlob;
        var link = document.createElement("a"); // Or maybe get it from the current document
        link.href = recordedBlob.blobURL;
        link.download = "aDefaultFileName.txt";
        link.innerHTML = "Click here to download the file";
        document.body.appendChild(link);
    }

    onSave(recordedBlob) {
        console.log("saving", recordedBlob);
    }

    render() {
        return (
            <div className={"meeting"}>
                <div className={""}>
                    <div className={"react-mic"}>
                        <ReactMic
                            record={this.state.record}
                            className="sound-wave"
                            onStop={this.onStop}
                            onData={this.onData}
                            onSave={this.onSave}
                            strokeColor="#000000"
                            backgroundColor="#FF4081" />
                    </div>
                    <div className={"react-mic-control"}>
                        <button className={"btn btn-success"} onTouchTap={this.startRecording} onClick={this.startRecording} type="button">Start</button>
                        <button className={"btn btn-danger"} onTouchTap={this.stopRecording} onClick={this.stopRecording} type="button">Stop</button>
                    </div>
                    <div><audio id="audio" controls></audio></div>
                </div>
            </div>
        );
    }
}

export default Meeting;
