import React, { Component } from 'react';
import $ from 'jquery';
import './Login.css';
import lock from './lock.svg';
import { Link } from 'react-router-dom';

class Login extends Component {
    // state = {};
    constructor(props) {
        super(props);
        var valid
        this.state = {
            valid: false
        }
        console.log("entring");
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleSubmit = () => {
        // this.props.uname = document.getElementById("usr").value;
        // this.props.mtime = document.getElementById("mt").value;
        $.ajax({
            url: 'http://localhost:8888/voinotesdb.php',
            type: 'POST',
            data: {'uid': document.getElementById("usr").value},
            success: (data) => {
                if (data === document.getElementById("pas").value) {
                    console.log("perfect");
                    this.setState((state) => {
                        return {valid: true};
                    });
                } else {
                    console.log("che");
                    this.setState((state) => {
                        return {valid: false};
                    });
                }
            },
            error: function (xhr, status, err) {
                console.log("Error reported by AJAX " + err);
            }
        });
    }
    render() {
        return (
            <div className={"container"}>
                <div className={"jumbotron"}>
                    <div className="Login">
                        <header className="Login">
                            <img src={lock} id="lock" alt="lock"></img>
                            <center>
                                <p> Login </p>
                                <form action="#" method="POST">
                                    <input type="text" id="usr" name="user" placeholder="Username" /><br></br>
                                    <input type="password" id="pas" name="pass" placeholder="Password" /><br></br><br></br>
                                    Meeting Time: <input type="time" id="mt" name="mtime" min="9:00" max="20:00" /><br></br><br></br>
                                    {/* <input type="submit" name="submit" value="Start" className="btn btn-success" /> */}
                                    <Link to="#" className={"btn btn-success"} onClick={this.handleSubmit}>Start</Link>
                                    {alert(this.state.valid)}
                                </form>
                            </center>
                        </header>
                    </div>
                </div>
            </div>
        )
    }
}

export default Login;
