import React, { Component } from 'react';
import './Meeting.css';
// import Login from './Login.js';
import { ReactMic } from 'react-mic';
import { Redirect } from 'react-router';

function LoginRedirect(props) {
    if (props.fromLogin === true) {
        console.log("red");
        return <Redirect to="/meeting" />
    } else {
        return <Redirect to="/" />
    }
}

class Meeting extends Component {

    constructor(props) {
        super(props);

        let fl, un, mt;
        try {
            fl = this.props.location.state.fromLogin;
            un = this.props.location.state.uname;
            mt = this.props.location.state.mtime;

            // this.props.location.state.fromLogin = false;
            // this.props.location.state.uname = "";

            // console.log(this.props.location.state.fromLogin);
        } catch (TypeError) {
            console.log("m error");
            fl = false;
            un = "";
            mt = "";
        }

        this.state = {
            fromLogin: fl,
            uname: un,
            mtime: mt,
            ctime: "",
            record: false
        }
    }

    componentDidMount = () => {
        setInterval(() => {
            var d = new Date();
            var ch = d.getHours();
            var cm = d.getMinutes();

            if (ch < 10) {
                ch = "0" + ch;
            }
            if (cm < 10) {
                cm = "0" + cm;
            }

            var curt = ch + ":" + cm;
            this.setState({
                ctime: curt
            });
            console.log(this.state.ctime);
        }, 1000);
        console.log(typeof (curt));
        console.log(curt);
        console.log(this.state.mtime);
        console.log(curt === this.state.mtime);
    }

    startRecording = () => {
        this.setState({
            record: true
        });
    }

    stopRecording = () => {
        this.setState({
            record: false
        });
    }

    onData(recordedBlob) {
        console.log('chunk of real-time data is: ', recordedBlob);
    }

    onStop(recordedBlob) {
    }
    render() {
        return (
            <div className={"meeting"}>
                <div className={""}>
                    <LoginRedirect fromLogin={this.state.fromLogin} uname={this.state.uname} />
                    <div className={"jumbotron text-left"} id="mjumbo">Welcome, {this.state.uname}</div>
                    <div id="clk"></div>
                    <div className={"react-mic"}>
                        <ReactMic
                            record={this.state.record}
                            className="sound-wave"
                            onStop={this.onStop}
                            onData={this.onData}
                            strokeColor="#000000"
                            backgroundColor="#FF4081" />
                    </div>
                    <div className={"react-mic-control"}>
                        <button className={"btn btn-success"} onClick={this.startRecording} type="button">Start</button>
                        <button className={"btn btn-danger"} onClick={this.stopRecording} type="button">Stop</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default Meeting;
