import React, { Component } from 'react';
import $ from 'jquery';
import './Login.css';
import lock from './lock.svg';
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router';

function MeetingRedirect(props)  {
    if(props.valid === true){
        return <Redirect to={{ pathname: "/meeting", state: { valid: props.valid }}} />
    }
    else{
        return <Redirect to={{ pathname: "/login", state: { valid: props.valid }}} />
    }
}

class Login extends Component {
    constructor(props) {
        super(props);

        console.log(this.props.location.state.valid);
        this.state = {
            valid: this.props.location.state.valid
        }

        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleSubmit = () => {
        $.ajax({
            url: 'http://localhost:8888/voinotesdb.php',
            type: 'POST',
            data: {'uid': document.getElementById("usr").value},
            success: (data) => {
                console.log(data[0]);
                if (data['Mgr_Pass'] === document.getElementById("pas").value) {
                    console.log("perfect");
                    this.setState((state) => ({valid: true}));
                    // this.setState((state) => ({link: "/meeting"}));
                    // meetingRedirect = <Redirect to="/meeting" />
                    // this.setState((state) => ({meetingRedirect: <Redirect to="/meeting" />}));
                } else {
                    console.log("che");
                    this.setState((state) => ({valid: false}));
                    // this.setState((state) => ({link: "/login"}));
                    // this.setState((state) => ({meetingRedirect: <Redirect to="/login" />}));
                }
            },
            error: function (xhr, status, err) {
                console.log("Error reported by AJAX " + err);
            }
        });
        // if(this.state.valid) {
        //     meetingRedirect = <Redirect to="/meeting" />
        // } else {
        //     meetingRedirect = <Redirect to="#" />
        // }
    }

    render() {
        return (
            <div className={"container"}>
                <div className={"jumbotron"}>
                    <div className="Login">
                        <header className="Login">
                            <img src={lock} id="lock" alt="lock"></img>
                            <center>
                                <p> Login </p>
                                <form action="/login" method="POST">
                                    <input type="text" id="usr" name="user" placeholder="Username" /><br></br>
                                    <input type="password" id="pas" name="pass" placeholder="Password" /><br></br><br></br>
                                    Meeting Time: <input type="time" id="mt" name="mtime" min="9:00" max="20:00" /><br></br><br></br>
                                    <Link to="#" className={"btn btn-success"} onClick={this.handleSubmit}>Start</Link>
                                    {/* <input type="submit" name="submit" value="Start" className="btn btn-success" /> */}
                                    {/* <button className={"btn btn-success"} onClick={this.handleSubmit}>Start</button> */}
                                    {/* {alert(this.state.link)} */}
                                    {/* {alert(this.state.meetingRedirect)} */}
                                    {/* {this.state.meetingRedirect} */}
                                    <MeetingRedirect valid={this.state.valid} name/>
                                </form>
                            </center>
                        </header>
                    </div>
                </div>
            </div>
        )
    }
}

export default Login;
