import React, { Component } from 'react';
import './Meeting.css';
import $ from 'jquery';
// import Login from './Login.js';
import { ReactMic } from 'react-mic';

class Meeting extends Component {

    constructor(props) {
        super(props);
        this.state = {
            record: false
        }
    }

    handleClick = () => {
        $.ajax({
            url: 'http://localhost:8888/voinotesdb.php',
            type: 'GET',
            success: function(data) {
                $("#test").html(data);
                console.log("Data returned by AJAX " + data);
            }.bind(this),
            error: function(xhr, status, err) {
                console.log("Error reported by AJAX " + err);
            }.bind(this)
        });
    }

    startRecording = () => {
        this.setState({
            record: true
        });
    }

    stopRecording = () => {
        this.setState({
            record: false
        });
    }

    onData(recordedBlob) {
        console.log('chunk of real-time data is: ', recordedBlob);
    }

    onStop(recordedBlob) {
        // this.setState
    }
    render() {
        return (
            <div className={"meeting"}>
                <div className={""}>
                    <div>{console.log("Username : ", this.props.uname)}</div>
                    <div className={"react-mic"}>
                        <ReactMic
                            record={this.state.record}
                            className="sound-wave"
                            onStop={this.onStop}
                            onData={this.onData}
                            strokeColor="#000000"
                            backgroundColor="#FF4081" />
                    </div>
                    <div className={"react-mic-control"}>
                        <button className={"btn btn-success"} onClick={this.startRecording} type="button">Start</button>
                        <button className={"btn btn-danger"} onClick={this.stopRecording} type="button">Stop</button>
                        <button onClick={this.handleClick}>Sample</button>
                    </div>
                    <div id="test">Sample text</div>
                </div>
            </div>
        );
    }
}

export default Meeting;
